mapper.select:
SELECT BOARD_NO , BOARD_TITLE , BOARD_CONTENT , BOARD_WRITER , BOARD_CREATEDATE , CATE_NO , ROW_NUM FROM ( SELECT BOARD_NO , BOARD_TITLE , BOARD_CONTENT , BOARD_WRITER , BOARD_CREATEDATE , CATE_NO , ROWNUM AS ROW_NUM FROM ( SELECT BOARD_NO , BOARD_TITLE , BOARD_CONTENT , BOARD_WRITER , TO_CHAR(BOARD_CREATEDATE, 'YYYY.MM.DD') AS BOARD_CREATEDATE , CATE_NO FROM BOARD
WHERE ${searchKeyWord} LIKE '%'||#{searchValue}||'%'
ORDER BY BOARD_NO DESC ) ) WHERE ROW_NUM >= #{startNum} AND ROW_NUM <= #{endNum}

mapper.insert:
INSERT INTO BOARD (
	BOARD_NO
	, BOARD_TITLE
	, BOARD_CONTENT
	, BOARD_WRITER
	, BOARD_CREATEDATE
	, CATE_NO
) VALUES (
	(SELECT NVL(MAX(BOARD_NO), 0) + 1 FROM BOARD)
	, #{boardTitle}
	, #{boardContent}
	, ${boardWriter}
	, ${boardDate}
	, ${cateNo}
)

INSERT INTO COMM (
	COMM_NO
	, BOARD_NO
	, COMM_CONTENT
	, COMM_CREATEDATE
	, COMM_WRITER
) VALUES (
	(SELECT NVL(MAX(BOARD_NO), 0) + 1 FROM BOARD)
	, #{boardNo}
	, #{commContent}
	, #{commDate}
	, #{commWriter}
)
