mapper.insert:
INSERT INTO CHANGE_MAJOR
(CHANGE_ID
	, STATUS
	, INSERT_DATE
	, UPDATE_DATE
	, REASON
	, STU_NO
	, COLL_NO
	, DEPT_ID
)VALUES(
(SELECT NVL(MAX(TO_NUMBER(SUBSTR(CHANGE_ID, 5, 3))) + 1, 1) FROM CHANGE_MAJOR) AS CHANGE_ID
	, #{status}
	, #{insertDate}
	, #{updateDate}
	, #{reason}
	, #{stuNo}
	, #{collNo}
	, #{deptId}
)
