mapper.insert:
INSERT INTO CHANGE_MAJOR
(CHANGE_ID
	, STATUS
	, INSERT_DATE
	, UPDATE_DATE
	, REASON
	, STU_NO
	, COLL_NO
	, DEPT_ID
	, UP_COLL
	, UP_DEPT
)VALUES(
(SELECT 'CHANGE_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(CHANGE_ID, 8, 3))), 0)+1, 3, '0') FROM CHANGE_MAJOR)
	, #{status}
	, #{insertDate}
	, #{updateDate}
	, #{reason}
	, #{stuNo}
	, #{collNo}
	, #{deptId}
	, #{upColl}
	, #{upDept}
)
