mapper.select:
SELECT S.YEAR , S.SEMESTER , S.SEM_ID , SG.LEC_ID , L.LEC_NAME , L.LEC_CREDIT , SG.STU_NO , SG.GRADE , G.SCORE FROM STU_GRADE SG , LECTURE L , GRADE G, SEMESTER S WHERE SG.LEC_ID = L.LEC_ID AND SG.GRADE = G.GRADE AND SG.SEM_ID = S.SEM_ID
AND S.SEM_ID = #{semId}
AND SG.STU_NO = #{stuNo}

SELECT S.YEAR , S.SEMESTER , S.SEM_ID , SG.LEC_ID , L.LEC_NAME , L.LEC_CREDIT , SG.STU_NO , SG.GRADE , G.SCORE FROM STU_GRADE SG , LECTURE L , GRADE G, SEMESTER S WHERE SG.LEC_ID = L.LEC_ID AND SG.GRADE = G.GRADE AND SG.SEM_ID = S.SEM_ID AND SG.GRADE != 'F'
AND S.SEM_ID = #{semId}
AND SG.STU_NO = #{stuNo}

SELECT SUM(L.LEC_CREDIT) LEC_CREDIT , TO_CHAR(AVG(G.SCORE),99.99) SCORE FROM STU_GRADE SG , LECTURE L , GRADE G, SEMESTER S WHERE SG.LEC_ID = L.LEC_ID AND SG.GRADE = G.GRADE AND SG.SEM_ID = S.SEM_ID AND SG.GRADE != 'F'
AND S.SEM_ID = #{semId}
AND SG.STU_NO = #{stuNo}

mapper.insert:
INSERT INTO STU_GRADE (
	SUBJECT_ID
	,LEC_ID
	,STU_NO
	,SEM_ID
) VALUES(
	#{stuNo}||#{lecId}||(SELECT TO_CHAR(SYSDATE, 'YYYY')||CASE WHEN TO_CHAR(SYSDATE, 'MM') >=7 THEN '2' ELSE '1' END SEM_ID FROM DUAL)
	,#{lecId}
	,#{stuNo}
	,(SELECT TO_CHAR(SYSDATE, 'YYYY')||CASE WHEN TO_CHAR(SYSDATE, 'MM') >=7 THEN '2' ELSE '1' END SEM_ID FROM DUAL)
)
